function read_dir($dir){
        
    $path = str_replace(PATH_FILE, '', $dir);
    if(is_dir($dir)) {
        $dir_array = scandir($dir);
        foreach($dir_array as $file) {
          if(stripos($file, '.') > 0) {
            echo "video filename:  <a href='".SITE_ROOT."$path$file'>{$file}</a><br />";
          }
        }
      }
    }